installer-gui-script.script:
function installationCheck() {
	if(!(system.compareVersions(system.version.ProductVersion, '10.5') >= 0)) {
		my.result.title = system.localizedString('Cannot install MyData');
		my.result.message = system.localizedString('This package requires OS X 10.5 or higher.');
		my.result.type = 'Fatal';
		return false;
	}
}
